import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import React, { useState, useEffect } from 'react';

function Navigation() {
    const [isAuth, setIsAuth] = useState(false);

    useEffect(() => {
        const access_token = localStorage.getItem('access_token');
        if (access_token !== null) {
            setIsAuth(true);
        } else {
            setIsAuth(false);
        }
    }, []);

    const handleLogin = () => {
        // Aquí se realiza la lógica de inicio de sesión y se establece el estado de autenticación correctamente

        // Luego de iniciar sesión, se recarga la página automáticamente
        window.location.reload();
    };

    return (
        <div>
            <Navbar bg="dark" variant="dark">
                <Navbar.Brand href="/">Guasac 2</Navbar.Brand>
                <Nav className="me-auto">
                    {isAuth && <Nav.Link href="/">Home</Nav.Link>}
                </Nav>
                <Nav>
                    {isAuth ? (
                        <Nav.Link href="/logout">Logout</Nav.Link>
                    ) : (
                        <Nav.Link onClick={handleLogin}>Login</Nav.Link>
                    )}
                </Nav>
            </Navbar>
        </div>
    );
}

export default Navigation;
